sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading && !g_form.isNewRecord() || newValue === '' || newValue == oldValue) {
      return;
   }

	var ga = new GlideAjax('GetEmailAddress');
	ga.addParam('sysparm_name', 'getEmail');
	ga.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
	ga.getXML(callBack);


	function callBack(response){
			var emailAddress = response.responseXML.documentElement.getAttribute('answer');
			g_form.clearValue('u_requested_for_email');
			g_form.setValue('u_requested_for_email', emailAddress);
	}   
}
